<?php
/*
Page: In app purchase for ios
Description: this functionality is used for get payment details and verify user's payment is running or expired
Parameters: receipt-data, password [Both parameters will taken from ios developer]
*/

$url = 'https://sandbox.itunes.apple.com/verifyReceipt';
$receipt = "";

$password= "8a7cdae57fd546cc8ddc6c5b90b79aaa";
$encodedData = json_encode( Array( 
    'receipt-data' => $receipt,
	'password' => $password
) );


//Open a Connection using POST method, as it is required to use POST method.
$ch = curl_init();
curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
curl_setopt($ch, CURLOPT_URL, $url);
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
curl_setopt($ch, CURLOPT_POST, true);
curl_setopt($ch, CURLOPT_POSTFIELDS, $encodedData);
$encodedResponse = curl_exec($ch);
curl_close($ch);

$response = json_decode( $encodedResponse );


echo "Status Code:";

echo $response->{'status'};

if ($response->{'status'} != 0) {
    echo "FAIL";
} else {
	echo "Success <br>";
    echo "<pre>";
	print_r($response);
}
